import './Criar.css'
import React from 'react';

export default function Criar(props){

    // const [projeto,setProjeto] = useState({});

    // function handleImputChange(e){
    //     setProjeto({...projeto,[e.target.name]: e.target.value});
    // }

    // function handleSubmit(e){
    //     e.preventDefault();
    //     props.setProjeos(props.projetos.concat(projeto));
    // }
  
    // const[nomeProduto, setNomeProduto] = useState()
    // const[descProduto, setDescProduto] = useState()
    // const[categoria, setCategoria] = useState()
    // const[tamanho, setTamanho] = useState()
    // const[quantidade, setQuantidade] = useState()
    // const[preco, setPreco] = useState()
  
    return (
        <>
            <div id="Vender">
                <h1 className='Titulo'>Produtos</h1>
                <form id="form_produto" className="Add_form">
                    <label>Nome do produto:</label>
                    <input 
                        type="text" 
                        // onChange={handleImputChange}     
                        name="nomeProduto" 
                        className="form-control"  
                        placeholder="Nome"
                        onChange={(e) => props.setNomeProduto(e.target.value)}
                    />
                    <label>Descrição:</label>
                    <input 
                        type="text" 
                        // onChange={handleImputChange} 
                        name="descProduto" 
                        className="form-control"  
                        placeholder="Descrição do Produto"
                        onChange={(e) => props.setDescProduto(e.target.value)}
                    />
                    <label>Quantidade:</label>
                    <input 
                        type="number" 
                        // onChange={handleImputChange} 
                        name="quantidade" 
                        className="form-control" 
                        placeholder="Defina a quantidade de peças"
                        onChange={(e) => props.setQuantidade(e.target.value)}
                    />


                    <label>Valor:</label>
                    <input 
                        type="text" 
                        // onChange={handleImputChange}  
                        name="preco" 
                        className="form-control"  
                        placeholder="Preço do produto"
                        onChange={(e) => props.setPreco(e.target.value)}
                    />
                  
                
                  <label>Categoria:</label>
                        <select 
                        // onChange={handleImputChange}
                        onChange={(e) => props.setCategoria(e.target.value)}
                        name="categoria"
                        >
                            <option value="Calça">Calça</option>
                            <option value="Camisa">Camisa</option>
                            <option value="Jaqueta e Casacos">Jaqueta e Casacos</option>
                            <option value="Tenis">Tênis</option>
                        </select>

                        <label>Tamanho:</label>
                        <select 
                        // onChange={handleImputChange}
                        onChange={(e) => props.setTamanho(e.target.value)}
                        name="tamanho"
                        >
                            <option value="PP">PP</option>
                            <option value="P">P</option>
                            <option value="M">M</option>
                            <option value="GG">G</option>
                            <option value="GG">GG</option>
                        </select>
                  
                  <br></br>
                    <input type="submit" value="Anunciar"  />

    
                </form>
            </div>
        </>
    );
}
