import './App.css';
import React,{useState} from 'react'
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Home from './pages/HomePag'
import Anunciar from './pages/AnunciarPag'
import Perfil from './pages/PerfilPag'
import Header from './components/Header/Header';
import Footer from './components/Footer/Footer'
import ProdutoPag from './pages/ProdutoPag';


function App(props) {

    const[produto, setProduto] = useState(
      [{
        senha:1,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      },
      {
        senha:2,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      },
      {
        senha:3,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      },
      {
        senha:4,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      },
      {
        senha:5,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      },
      {
        senha:6,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      },
      {
        senha:7,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      },
      {
        senha:8,
        nomeProduto:'Calça Verde',
        descProduto:'Calça muito verde, tão verde que se camufla em árvores de todo o deserto',
        tamanho:'P',
        categoria:'Calça',
        preco:'249.99'
      }]
    );

  return (
    <Router>
      <Header/>
      <Routes>
        <Route path="/" element={<Home produto={produto} 
                                       setProduto={setProduto}
                                       nomeProduto={props.produto.nomeProduto}
                                       descProduto={props.produto.descProduto}
                                       preco={props.produto.preco}
                                       quantidade={props.produto.quantidade}
                                       categoria={props.produto.categoria}
                                       tamanho={props.produto.tamanho}/>}/>
        <Route path="/Perfil" element={<Perfil/>}/> 
        <Route path="/Anunciar" element={<Anunciar setProduto={setProduto} produto={produto}/>} />
        <Route path="/ProdutoPag" element={<ProdutoPag produto={produto}/>} /> 
      </Routes>  
      <Footer/>
    </Router>
  );
}

export default App;















