import './Perfil.css'
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { addBox } from '../Redux/boxSlice';
function Produto(){


    const [produto, setProduto] = useState();
    const dispatch = useDispatch();

function handleImputChange(e){
    setProduto({...produto,[e.target.name]: e.target.value});
}

function handleSubmit(e){
    e.preventDefault();
    dispatch(addBox(produto))
    // props.setProduto(props.produto.concat(produto));
}
    return(


        <main>
        <div className="container">
            <div className="card" id="borda">
            <form id="form_produto" className="Add_form">
                        <select 
                        onChange={handleImputChange}
                        name="categoria"
                        >
                        <p className="preco">R$ <span className="bold">{produto.preco}</span></p>
                        </select>
                        <select 
                        onChange={handleImputChange}
                        name="categoria"
                        >
                        <h2 className="nome-produto">apenas</h2>
                        </select>
                        </form>
                        </div>
                    </div>
        </main>
    )
}

export default Produto
