import { createSlice } from '@reduxjs/toolkit'

const initialProduto2 =
    [{
      id: 0,
      nomeProduto:'AAAAA',
      descProduto:'APENAS',
      tamanho:'P',
      categoria:'Calça',
      preco: '664.99'
    }];

// function openBoxReducer(produtos, senha, produto){
//     var filtro = (struct) => struct.id = senha
//     let thisId = produtos.filter(filtro);    
//     return produtos.filter([{...produto, id: thisId}]);
// }

function  openBoxReducer(produtos, produto){
    let index = produtos.map(produto => produto.id).indexOf(produto.id);
    produtos.splice(index, 1, produto);
    return produtos;
}

export const openBoxSlice = createSlice({
    name:'openBox',
    initialState: initialProduto2,
       
    reducers: {
        openBox: (state, action) => openBoxReducer (state,action.payload)
    }       
})

export const { openBox } = openBoxSlice.actions
export default openBoxSlice.reducer
