      tamanho:'P',
      categoria:'Calça',
      preco:'249.99'
    }];

function openBoxReducer(produtos, senha){
    var filtro = (struct) => struct.id = senha
    var selecao = produtos.filter(filtro)
    
    return produtos = selecao;
}

function addBoxReducer(produtos, produto){
    let proxId = 1 + produtos.map(p => p.id).reduce((x,y) => Math.max(x,y));
    return produtos.concat([{...produto, id: proxId}]);
}

export const boxSlice = createSlice({
    name:'box',
    initialState: initialProduto,
       
    reducers: {
        openBox: (state, senha) => openBoxReducer (state, senha),
        addBox: (state , action) => addBoxReducer (state, action.payload)
    }       
})

export const { addBox, openBox } = boxSlice.actions
export default boxSlice.reducer
