import './Perfil.css'
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { addBox } from '../Redux/boxSlice';
import React, {useState} from 'react';

function Produto(){


    const [produto, setProduto] = useState();
    const dispatch = useDispatch();

function handleImputChange(e){
    setProduto({...produto,[e.target.name]: e.target.value});
}

function handleSubmit(e){
    e.preventDefault();
    dispatch(addBox(produto))
    // props.setProduto(props.produto.concat(produto));
}
    return(


        <main>
        <div className="container">
            <div className="card" id="borda">
                <label><h2 className="nome-produto">Email:</h2></label>
                    <input 
                        type="text" 
                        onChange={handleImputChange}     
                        name="nome-produto" 
                        className="form-control"  
                        placeholder="Nome"
                    />
                <label><h2>Senha:</h2></label>
                    <input 
                        type="text" 
                        onChange={handleImputChange}     
                        name="nome-produto" 
                        className="form-control"  
                        placeholder="Nome"
                    />
                        <p className="preco">R$ <span className="bold">dois</span></p>
   
                        <h2 className="nome-produto">apenas</h2>

                        </div>
                    </div>
        </main>
    )
}

export default Produto
