 import { configureStore } from '@reduxjs/toolkit'
 import boxSlice from './boxSlice'
 import openBox from './openBoxSlice'

import { persistStore, persistReducer, FLUSH, REHYDRATE, PAUSE, PERSIST, PURGE, REGISTER } from 'redux-persist';
import storage from 'redux-persist/lib/storage';
import autoMergeLevel2 from 'redux-persist/es/stateReconciler/autoMergeLevel2';

import productsReducer from './reducers/BoxSlice';


// const store = configureStore({
//     reducer:{
//         box: boxSlice
//     }
// })

// export default store

export const store = configureStore({
    reducer: {
        products: persistReducer(productsPersistConfig, productsReducer),
        bookings: persistReducer(bookingsPersistConfig, bookingsReducer),
        sellers: persistReducer(sellersPersistConfig, sellersReducer),
        user: persistReducer(userPersistConfig, userReducer),
    }
})


export const persistor = persistStore(store);
